import { Component } from "@angular/core";
import { WeatherApiService } from "../../services/weather-api.service";

@Component({
  selector: "location-search",
  templateUrl: "./location-search.component.html",
  styleUrls: ["./location-search.component.css"],
})
export class LocationSearchComponent
{

  locationText: string = "";

  constructor(private api: WeatherApiService) { }

  search(): void
  {
    if (this.locationText == null)
      return;

    this.api.setLocation(this.locationText);
    this.api.getWeather();
  }

}
<span id="locationSearch">

  <input (keydown.enter)="search()" [(ngModel)]="locationText" type="search" placeholder="Enter your city, or postcode" />

  <span id="searchBtn" (click)="search()">
    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
