import { Component } from "@angular/core";

@Component({
    selector: "location-search",
    templateUrl: "./location-search.component.html",
    styleUrls: ["./location-search.component.css"],
})
export class LocationSearchComponent {
    search(): void {
        console.log("searching!");
    }
}
<span id="locationSearch">
  <input type="search" placeholder="Enter your city, or postcode" />
  <span id="searchBtn">
    <svg (click)="search()" xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-search" viewBox="0 0 16 16">
      <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z" />
    </svg>
  </span>
</span>
