import { Component } from '@angular/core';
import { WeatherApiService } from "../../services/weather-api.service";

@Component({
  selector: 'forecast-display',
  templateUrl: './forecast-display.component.html',
  styleUrls: ['./forecast-display.component.css']
})
export class ForecastDisplayComponent
{

  constructor(private api: WeatherApiService) { }

  /**
   * Retrieves the weather updates. 
   */
  async getWeatherUpdates(): Promise<any>
  {
    let updates;

    await WeatherApiService.weatherUpdates.subscribe((value) =>
    {
      updates = value;
    });

    return updates;
  }

}
<div id="forecast-display">
  <h3>Location</h3>
  <ul>
    <li *ngIf="(getWeatherUpdates() | async)">{{ getWeatherUpdates().current['temp_c'] | async }}</li>
  </ul>
  <h3>Weather</h3>

</div>
