import { Component } from '@angular/core';
import { WeatherApiService } from "../../services/weather-api.service";

@Component({
  selector: 'forecast-display',
  templateUrl: './forecast-display.component.html',
  styleUrls: ['./forecast-display.component.css']
})
export class ForecastDisplayComponent
{

  public forecastData: any;

  constructor(private api: WeatherApiService) { }

  ngOnInit()
  {
    WeatherApiService.weatherUpdates.subscribe((data) =>
    {
      this.forecastData = data;
    });

    this.api.getWeather();
  }

}
<div id="forecast-display">
  <div *ngIf="!forecastData">
    Loading weather forecast...
  </div>

  <div class="d-flex" *ngIf="forecastData && forecastData.isValid">
    <div class="border">
      <h3>Location</h3>
      <ul>
        <li>Name: {{ forecastData.location.name }}</li>
        <li>Region: {{ forecastData.location.region }}</li>
        <li>Country: {{ forecastData.location.country }}</li>
      </ul>
    </div>
    <div class="border">
      <h3>Weather</h3>
      <ul>
        <li>{{ forecastData.current['temp_c'] }}°C</li>
      </ul>
    </div>
  </div>
</div>
