import { Injectable } from '@angular/core';
import { HttpClient } from "@angular/common/http";
import { Subject } from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class WeatherApiService
{

  public static WeatherUpdates = weatherUpdates.asObservable();

  /**This is a Subject which posts whenever the weather data is updated. */
  protected static weatherUpdates = new Subject<any>();

  /** Stores the location for the Weather API. */
  protected static location: string = "London";

  constructor(private http: HttpClient) { }

  /**
   * Retrieves a WeatherResponse from the back-end API via a GET request. 
   * @returns
   * @author Sam Burford
   */
  getWeather()
  {
    return this.http.get("https://localhost:7185/api/weather").subscribe((res) =>
    {
      WeatherApiService.weatherUpdates.next(res);
      console.log(res);
    });
  }

}
